#INCLUDE <RT-C64-08-9F.H>EXTERN _FASTCALL _CHROUT() *= 0XFFD2 ;EXTERN _FASTCALL CHAR _CHRIN() *= 0XFFCF ;EXTERN _FASTCALL _GETIN() *= 0XFFE4 ;EXTERN _FASTCALL INT __CHKIN() *= 0XFFC6 ;EXTERN _FASTCALL INT __CHKOUT() *= 0XFFC9 ;INT _CHKIN(LFN)INT LFN;Û RETURN(__CHKIN(LFN<<8)); ÝINT _CHKOUT(LFN)INT LFN;Û RETURN(__CHKOUT(LFN<<8)); ÝEXTERN _FASTCALL _CLALL() *= 0XFFE7 ;EXTERN _FASTCALL _CLOSE() *= 0XFFC3 ;EXTERN _FASTCALL _CLRCHN() *= 0XFFCC ;EXTERN _FASTCALL __OPEN() *= 0XFFC0 ;EXTERN CHAR _KERNAL_FNAM_LEN *= 0XB7;EXTERN CHAR _KERNAL_LFN *= 0XB8;EXTERN CHAR _KERNAL_SA *= 0XB9;EXTERN CHAR _KERNAL_FA *= 0XBA;EXTERN INT _KERNAL_FNAM *= 0XBB;INT _OPEN(LFN, FA, SA, FNAM)CHAR LFN, FA, SA;CHAR *FNAM;Û  CHAR *P;  FOR(P=FNAM; *P; ++P);  _KERNAL_FNAM_LEN = P-FNAM;  _KERNAL_LFN = LFN;  _KERNAL_FA = FA;  _KERNAL_SA = SA;  _KERNAL_FNAM = FNAM;  __OPEN();ÝEXTERN _FASTCALL INT _READST() *= 0XFFB7 ;EXTERN _FASTCALL CHAR _ACPTR() *= 0XFFA5 ;EXTERN _FASTCALL _CIOUT() *= 0XFFA8 ;EXTERN _FASTCALL _LISTEN() *= 0XFFB1 ;EXTERN _FASTCALL _SECOND() *= 0XFF93 ;EXTERN _FASTCALL _TALK() *= 0XFFB4 ;EXTERN _FASTCALL _TKSA() *= 0XFF96 ;EXTERN _FASTCALL _UNLSN() *= 0XFFAE ;EXTERN _FASTCALL _UNTLK() *= 0XFFAB ;