#INCLUDE <RT-C64-0801.H>EXTERN _CHROUT() *= 0XFFD2 ;EXTERN CHAR _CHRIN() *= 0XFFCF ;EXTERN _GETIN() *= 0XFFE4 ;EXTERN INT __CHKIN() *= 0XFFC6 ;EXTERN INT __CHKOUT() *= 0XFFC9 ;INT _CHKIN(LFN)INT LFN;Û RETURN(__CHKIN(LFN>>8)); ÝINT _CHKOUT(LFN)INT LFN;Û RETURN(__CHKOUT(LFN>>8)); ÝEXTERN _CLALL() *= 0XFFE7 ;EXTERN INT _CLOSE() *= 0XFFC3 ;EXTERN _CLRCHN() *= 0XFFCC ;EXTERN INT _OPEN() *= 0XFFC0 ;INT OPEN(LFN,GA,SA,NAM)CHAR LFN,GA,SA;CHAR *NAM;Û  CHAR I,*P;  INT *Q;  FOR(P=NAM;*P;++P) ;  I=P-NAM ;  P=0XB7;  *P++ = I; *P++ = LFN; *P++ = SA;  *P++ = GA; *(Q=P) = NAM;  RETURN(_OPEN());ÝEXTERN INT _READST() *= 0XFFB7 ;EXTERN CHAR _ACPTR() *= 0XFFA5 ;EXTERN _CIOUT() *= 0XFFA8 ;EXTERN _LISTEN() *= 0XFFB1 ;EXTERN _SECOND() *= 0XFF93 ;EXTERN _TALK() *= 0XFFB4 ;EXTERN _TKSA() *= 0XFF96 ;EXTERN _UNLSN() *= 0XFFAE ;EXTERN _UNTLK() *= 0XFFAB ;